#include "Types.r"#include "Controls.r"#include "::include:EudoraTypes.r"#include "::tabmania.r"resource 'eTAB' (133, purgeable) {	'CSOm',	'Nick',	'Phot',	"Photo",	0,	12,	12,	{			// Object 0			controlObject {					objectFlagNone,					"",					"Select Photo…",					"",					0, 0, 1, kControlPushButtonProc, 0, coFit, behaveNavigation, "picture", "",		// Display a Nav Services Dialog and pass the FSSpecPtr to the 'picture' object					{							horizontal	{ alignCenter, tabObject, rfNone },							bottom 			{ 0, tabObject, relToBottom, rfNegativeDefaultMargin }					}			};						// Object 0			pictureObject {					objectFlagAcceptsFocus | objectFlagExportable,					"picture",																									// tag					"",																													// Field name					'PICT',																											// Drag flavor we'll accept					{							left   {  0, tabObject, relToLeft,	 rfDefaultMargin					},		// left coordinate							top    {  0, tabObject, relToTop,		 rfDefaultMargin					},		// top coordinate							right  {  0, tabObject, relToRight,	 rfNegativeDefaultMargin	},		// right coordinate							bottom {  0, 0, relToTop, rfNegativeDefaultMargin	}																				// bottom coordinate					}			};	}};